DROP TABLE IF EXISTS `sistema_usuarios`;
CREATE TABLE `sistema_usuarios` (
  `id` binary(16) NOT NULL,
  `order_id` int(11) DEFAULT NULL,
  `last_update` int(11) DEFAULT '0',
