-- Procedure structure for caja_operaciones_SEL
-- ----------------------------
DROP PROCEDURE IF EXISTS `caja_operaciones_SEL`;
DELIMITER ;;
CREATE  PROCEDURE `caja_operaciones_SEL`(IN `_id_empresa` VARCHAR(36), IN `_id_tipo` VARCHAR(36))
BEGIN
#SELECCION MODO
SET @es_modo_fiscal= (
	SELECT 
		e.es_modo_fiscal
	FROM sistema_empresas e
	WHERE e.id = UNHEX(_id_empresa)
);


#CONSULTA PRINCIPAL PRODUCTOS
IF (@es_modo_fiscal>0) THEN
	#MODO FISCAL		
	SELECT 
			HEX(o.id) as id, 
			o.order_id,
			o.last_update,
			HEX(o.id_empresa) as id_empresa,
			HEX(o.id_tipo_operacion) as id_tipo_operacion,
			so.nombre as tipo_operacion_nombre,
			o.numero,
			o.nro_control,
			DATE_FORMAT(o.fecha,'%Y-%m-%dT%TZ') as fecha,
			o.fecha_registro,
			o.descripcion,
			COALESCE(o.monto * lc.valor,0) as monto,
			COALESCE(o.monto * lc.valor,0) as monto_moneda,
			hex(o.id_cotizacion) as id_cotizacion,
			o.id_status,
			o.id_estado,
			hex(o.id_usuario) as id_usuario,
			u.nombre as usuario_nombre
	FROM caja_operaciones as o
	LEFT JOIN sistema_operaciones as so ON o.id_tipo_operacion=so.id
	LEFT JOIN sistema_usuarios as u ON o.id_usuario=u.id
	LEFT JOIN (
				SELECT 
					cd.id_cotizacion,
					cd.valor 
					FROM listas_cotizaciones_detail cd
					WHERE cd.id_moneda=UNHEX("11E93A6F6D57549E838500E04C6F7E24")
			) lc ON o.id_cotizacion=lc.id_cotizacion
	WHERE o.id_empresa=UNHEX(_id_empresa) AND o.id_tipo_operacion=UNHEX(_id_tipo)
	ORDER BY o.order_id DESC;
ELSE
	#MODO STANDARD		
	SELECT 
			HEX(o.id) as id, 
			o.order_id,
			o.last_update,
			HEX(o.id_empresa) as id_empresa,
			HEX(o.id_tipo_operacion) as id_tipo_operacion,
			so.nombre as tipo_operacion_nombre,
			o.numero,
			o.nro_control,
			DATE_FORMAT(o.fecha,'%Y-%m-%dT%TZ') as fecha,
			o.fecha_registro,
			o.descripcion,
			COALESCE(o.monto,0) as monto,
			COALESCE(o.monto,0) as monto_moneda,
			hex(o.id_cotizacion) as id_cotizacion,
			o.id_status,
			o.id_estado,
			hex(o.id_usuario) as id_usuario,
			u.nombre as usuario_nombre
	FROM caja_operaciones as o
	LEFT JOIN sistema_operaciones as so ON o.id_tipo_operacion=so.id
	LEFT JOIN sistema_usuarios as u ON o.id_usuario=u.id
	WHERE o.id_empresa=UNHEX(_id_empresa) AND o.id_tipo_operacion=UNHEX(_id_tipo)
	ORDER BY o.order_id DESC;
END IF;
END
;;
DELIMITER ;

-- ----------------------------
-- Procedure structure for caja_operaciones_UPD
-- ----------------------------
DROP PROCEDURE IF EXISTS `caja_operaciones_UPD`;
DELIMITER ;;
CREATE  PROCEDURE `caja_operaciones_UPD`(IN `_id` VARCHAR(36), IN `_order_id` INT(36), IN `_last_update` INT(36), IN `_id_empresa` VARCHAR(36), IN `_id_tipo_operacion` VARCHAR(36), IN `_numero` INT(10), IN `_nro_control` VARCHAR(10), IN `_fecha` DATETIME, IN `_fecha_registro` DATETIME, IN `_descripcion` VARCHAR(255), IN `_monto` DOUBLE(20,8), IN `_id_cotizacion` VARCHAR(36), IN `_id_status` VARCHAR(36), IN `_id_estado` VARCHAR(36),IN `_id_usuario` VARCHAR(36))
BEGIN
	SET @ultima_actualizacion=(SELECT c.last_update FROM caja_operaciones c WHERE c.id = UNHEX(_id) LIMIT 1);
	
	IF (@ultima_actualizacion<=_last_update) THEN
		UPDATE caja_operaciones as c
		SET
			
			c.last_update=_last_update,
			c.fecha=_fecha, 
			c.monto=_monto,
			c.id_status=_id_status,
			c.id_estado=_id_estado,
			c.id_usuario=UNHEX(_id_usuario),
			c.descripcion=_descripcion
			
		WHERE c.id=UNHEX(_id);
	END IF;
	SELECT ROW_COUNT() as result;
	
END
;;
DELIMITER ;

