-- ----------------------------
-- Procedure structure for cola_operaciones_detail_SEL
-- ----------------------------
DROP PROCEDURE IF EXISTS `cola_operaciones_detail_SEL`;
DELIMITER ;;
CREATE  PROCEDURE `cola_operaciones_detail_SEL`(IN `_id_operacion` VARCHAR(36))
BEGIN
	#SELECCION MODO
	SET @es_modo_fiscal= (
		SELECT 
			e.es_modo_fiscal
			FROM cola_operaciones_detail m
			INNER JOIN inventario_productos p ON m.id_producto=p.id
			INNER JOIN sistema_empresas e ON p.id_empresa=e.id
			WHERE m.id_operacion = UNHEX(_id_operacion) LIMIT 1
	);

	#CONSULTA PRINCIPAL MOVIMIENTO 
	IF (@es_modo_fiscal>0) THEN
		#MODO FISCAL

		#TIPO OPERACION
		SET @tipo_operacion=	(
			SELECT
						HEX(l.id) as id
			FROM cola_operaciones_detail m
			INNER JOIN sistema_operaciones o ON m.id_tipo_operacion=o.id
			INNER JOIN sistema_listas l ON o.id_tipo=l.id
			WHERE m.id_operacion = UNHEX(_id_operacion) LIMIT 1
		);
		
		#VALOR COTIZACION OPERACION
		SET @valor_bs=1;
		#VENTAS
		IF (@tipo_operacion='11E7C39A0EACD9A08F1A00E04C6F7E24') THEN
			SET @valor_bs= (
				SELECT 
						lc.valor
					FROM ventas_operaciones op
					INNER JOIN listas_cotizaciones_detail lc ON op.id_cotizacion=lc.id_cotizacion
					WHERE op.id=UNHEX(_id_operacion) AND (lc.id_moneda=UNHEX('11E93A6F6D57549E838500E04C6F7E24'))
			);
		END IF;
		
		#COMPRAS
		IF (@tipo_operacion='11E8F22292A5ED4B8FF600270E383B06') THEN
			SET @valor_bs= (
				SELECT 
						op.id
					FROM compras_operaciones op
			);
		END IF;

		#INVENTARIO
		IF (@tipo_operacion='11E8F2229B16CA478FF600270E383B06') THEN
			SET @valor_bs= (
				SELECT 
						op.id
					FROM inventario_operaciones op
			);
		END IF;

		#CONSULTA MOVIMIENTO
		SELECT 
					HEX(m.id) as id, 
					m.order_id,
					m.last_update,
					HEX(m.id_operacion) as id_operacion,
					HEX(m.id_tipo_operacion) as id_tipo_operacion,
