					@monto_impuesto:=COALESCE(((@monto*((m.valor_impuesto/100)+1))-@monto ) * @valor_bs,0) as monto_impuesto,
					COALESCE(m.costo * @valor_bs,0) as costo_moneda,
					COALESCE(m.precio * @valor_bs,0) as precio_moneda,
					COALESCE(@monto,0) as monto_moneda,
					COALESCE(@monto_impuesto,0) as monto_impuesto_moneda
					
		FROM cola_operaciones_detail as m
		LEFT JOIN inventario_productos as p ON m.id_producto=p.id
		LEFT JOIN sistema_listas as u ON p.id_unidad=u.id
		WHERE m.id_operacion = UNHEX(_id_operacion);
	ELSE
		#MODO FISCAL
		SELECT 
					HEX(m.id) as id, 
					m.order_id,
					m.last_update,
					HEX(m.id_operacion) as id_operacion,
					HEX(m.id_tipo_operacion) as id_tipo_operacion,
					HEX(m.id_empresa) as id_empresa,
					HEX(m.id_producto) as id_producto, 
					p.codigo as producto_codigo,
					p.nombre as producto_nombre, 
					u.nombre as producto_unidad, 
					HEX(m.id_impuesto) as id_impuesto,				
					COALESCE(m.cantidad,0) as cantidad,				
					COALESCE(m.costo ,0) as costo,
					COALESCE(m.precio ,0) as precio,				
					COALESCE(m.valor_impuesto ,0) as valor_impuesto,
					@monto:=COALESCE((m.precio * m.cantidad ),0) as monto,
					@monto_impuesto:=COALESCE(((@monto*((m.valor_impuesto/100)+1))-@monto ) ,0) as monto_impuesto,
					COALESCE(m.costo,0) as costo_moneda,
					COALESCE(m.precio,0) as precio_moneda,
					COALESCE(@monto,0) as monto_moneda,
					COALESCE(@monto_impuesto,0) as monto_impuesto_moneda
					
		FROM cola_operaciones_detail as m
		LEFT JOIN inventario_productos as p ON m.id_producto=p.id
		LEFT JOIN sistema_listas as u ON p.id_unidad=u.id
		WHERE m.id_operacion = UNHEX(_id_operacion);
	END IF;
END
