			o.numero,
			o.nro_control,
			DATE_FORMAT(o.fecha,'%Y-%m-%dT%TZ') as fecha,
			o.fecha_registro,
			HEX(o.id_destino) as id_destino,
			HEX(o.id_proveedor) as id_proveedor,
			p.nombre as proveedor_nombre,
			p.rif as proveedor_rif,
			HEX(o.id_tipo_documento) as id_tipo_documento,
			so.nombre as tipo_documento_nombre,
			o.nro_control_documento,
			o.nro_documento,
			o.fecha_documento,
			o.nro_factura_afectada,
			hex(o.id_doc_origen) as id_doc_origen,
			hex(o.id_cotizacion) as id_cotizacion,
			COALESCE(o.monto_exento,0) as monto_exento,
			COALESCE(o.base_imponible_tg,0) as base_imponible_tg,
			COALESCE(o.base_imponible_tr,0) as base_imponible_tr,
			COALESCE(o.base_imponible_ta,0) as base_imponible_ta,
			o.pct_alicuota_tg,
			o.pct_alicuota_tr,
			o.pct_alicuota_ta,
			@monto_iva_tg:=COALESCE((o.base_imponible_tg*(o.pct_alicuota_tg/100)),0) as monto_iva_tg,
			@monto_iva_tr:=COALESCE((o.base_imponible_tr*(o.pct_alicuota_tr/100)),0) as monto_iva_tr,
			@monto_iva_ta:=COALESCE((o.base_imponible_ta*(o.pct_alicuota_ta/100)),0) as monto_iva_ta,
			@total:=COALESCE((o.monto_exento+o.base_imponible_tg+@monto_iva_tg+o.base_imponible_tr+@monto_iva_tr+o.base_imponible_ta+@monto_iva_ta),0) as total,
			COALESCE(o.monto_exento,0) as monto_exento_moneda,
			COALESCE(o.base_imponible_tg,0) as base_imponible_tg_moneda,
			COALESCE(o.base_imponible_tr,0) as base_imponible_tr_moneda,
			COALESCE(o.base_imponible_ta,0) as base_imponible_ta_moneda,
			COALESCE(@monto_iva_tg,0) as monto_iva_tg_moneda,
			COALESCE(@monto_iva_tr,0) as monto_iva_tr_moneda,
			COALESCE(@monto_iva_ta,0) as monto_iva_ta_moneda,			
			COALESCE(@total,0) as total_moneda,
			o.pct_descuento,
			o.pct_adicional,
			o.id_status,
			o.id_estado,
			hex(o.id_usuario) as id_usuario,
			u.nombre as usuario_nombre,
			o.observacion
	FROM compras_operaciones as o
	LEFT JOIN sistema_operaciones as so ON o.id_tipo_operacion=so.id
	LEFT JOIN compras_proveedores as p ON o.id_proveedor=p.id
	LEFT JOIN sistema_usuarios as u ON o.id_usuario=u.id
	WHERE o.id_empresa=UNHEX(_id_empresa) AND o.id_tipo_operacion=UNHEX(_id_tipo)
	ORDER BY o.order_id DESC;
END IF;
END
;;
DELIMITER ;

