-- ----------------------------
-- Procedure structure for inventario_movimientos_SEL
-- ----------------------------
DROP PROCEDURE IF EXISTS `inventario_movimientos_SEL`;
DELIMITER ;;
CREATE  PROCEDURE `inventario_movimientos_SEL`(IN `_id_operacion` VARCHAR(36))
BEGIN
	#SELECCION MODO
	SET @es_modo_fiscal= (
		SELECT 
			e.es_modo_fiscal
			FROM inventario_movimientos m
			INNER JOIN inventario_productos p ON m.id_producto=p.id
			INNER JOIN sistema_empresas e ON p.id_empresa=e.id
			WHERE m.id_operacion = UNHEX(_id_operacion) LIMIT 1
	);

	#CONSULTA PRINCIPAL MOVIMIENTO 
	IF (@es_modo_fiscal>0) THEN
		#MODO FISCAL

		#TIPO OPERACION
		SET @tipo_operacion=	(
			SELECT
						HEX(l.id) as id
			FROM inventario_movimientos m
			INNER JOIN sistema_operaciones o ON m.id_tipo_operacion=o.id
			INNER JOIN sistema_listas l ON o.id_tipo=l.id
			WHERE m.id_operacion = UNHEX(_id_operacion) LIMIT 1
		);
		
		#VALOR COTIZACION OPERACION
		SET @valor_bs=1;
		#VENTAS
		IF (@tipo_operacion='11E7C39A0EACD9A08F1A00E04C6F7E24') THEN
			SET @valor_bs= (
				SELECT 
						lc.valor
					FROM ventas_operaciones op
					INNER JOIN listas_cotizaciones_detail lc ON op.id_cotizacion=lc.id_cotizacion
					WHERE op.id=UNHEX(_id_operacion) AND (lc.id_moneda=UNHEX('11E93A6F6D57549E838500E04C6F7E24'))
			);
		END IF;
		
		#COMPRAS
		IF (@tipo_operacion='11E8F22292A5ED4B8FF600270E383B06') THEN
			SET @valor_bs= (
				SELECT 
						op.id
					FROM compras_operaciones op
			);
		END IF;

		#INVENTARIO
		IF (@tipo_operacion='11E8F2229B16CA478FF600270E383B06') THEN
			SET @valor_bs= (
				SELECT 
						op.id
					FROM inventario_operaciones op
			);
		END IF;

		#CONSULTA MOVIMIENTO
		SELECT 
					HEX(m.id) as id, 
					m.order_id,
					m.last_update,
					HEX(m.id_operacion) as id_operacion,
					HEX(m.id_tipo_operacion) as id_tipo_operacion,
					HEX(m.id_empresa) as id_empresa,
					HEX(m.id_producto) as id_producto, 
					p.codigo as producto_codigo,
					p.nombre as producto_nombre, 
					u.nombre as producto_unidad, 
					HEX(m.id_impuesto) as id_impuesto,				
					COALESCE(m.cantidad,0) as cantidad,				
					COALESCE(m.costo * @valor_bs,0) as costo,
					COALESCE(m.precio * @valor_bs,0) as precio,				
					COALESCE(m.valor_impuesto * @valor_bs,0) as valor_impuesto,
					@monto:=COALESCE((m.precio * m.cantidad )* @valor_bs,0) as monto,
					@monto_impuesto:=COALESCE(((@monto*((m.valor_impuesto/100)+1))-@monto ) * @valor_bs,0) as monto_impuesto,
					COALESCE(m.costo * @valor_bs,0) as costo_moneda,
					COALESCE(m.precio * @valor_bs,0) as precio_moneda,
					COALESCE(@monto,0) as monto_moneda,
					COALESCE(@monto_impuesto,0) as monto_impuesto_moneda,
					COALESCE(m.pct_comision,0) as pct_comision
					
		FROM inventario_movimientos as m
		LEFT JOIN inventario_productos as p ON m.id_producto=p.id
		LEFT JOIN sistema_listas as u ON p.id_unidad=u.id
		WHERE m.id_operacion = UNHEX(_id_operacion);
	ELSE
		#MODO FISCAL
		SELECT 
					HEX(m.id) as id, 
					m.order_id,
					m.last_update,
					HEX(m.id_operacion) as id_operacion,
					HEX(m.id_tipo_operacion) as id_tipo_operacion,
					HEX(m.id_empresa) as id_empresa,
					HEX(m.id_producto) as id_producto, 
					p.codigo as producto_codigo,
					p.nombre as producto_nombre, 
					u.nombre as producto_unidad, 
					HEX(m.id_impuesto) as id_impuesto,				
					COALESCE(m.cantidad,0) as cantidad,				
					COALESCE(m.costo ,0) as costo,
					COALESCE(m.precio ,0) as precio,				
					COALESCE(m.valor_impuesto ,0) as valor_impuesto,
					@monto:=COALESCE((m.precio * m.cantidad ),0) as monto,
					@monto_impuesto:=COALESCE(((@monto*((m.valor_impuesto/100)+1))-@monto ) ,0) as monto_impuesto,
					COALESCE(m.costo,0) as costo_moneda,
					COALESCE(m.precio,0) as precio_moneda,
					COALESCE(@monto,0) as monto_moneda,
					COALESCE(@monto_impuesto,0) as monto_impuesto_moneda,
					COALESCE(m.pct_comision,0) as pct_comision
					
		FROM inventario_movimientos as m
		LEFT JOIN inventario_productos as p ON m.id_producto=p.id
		LEFT JOIN sistema_listas as u ON p.id_unidad=u.id
		WHERE m.id_operacion = UNHEX(_id_operacion);
	END IF;
END
;;
DELIMITER ;

