		i.valor as impuesto_valor,
		p.nombre, 
		p.codigo,
		@costo:=COALESCE(lm.costo*lm.valor, COALESCE(p.costo*@last_valor_bs,0)) as costo,
		@precio:= IF( lm.costo, COALESCE(@costo * ((@pct_fiscal/100)+1),0), COALESCE(p.precio*@last_valor_bs,0) ) as precio,
		IF( lm.costo, @pct_fiscal, COALESCE(((@precio-@costo)/@costo)*100,0) ) as pct_utilidad,
		COALESCE(@costo,0) as costo_moneda,
		COALESCE(@precio,0) as precio_moneda,
		COALESCE(p.existencia,0) as existencia,
		COALESCE(p.pct_comision,0) as pct_comision,
		p.es_inactivo,
		p.imagen,
		p.nombre_web,
		p.descripcion_web,
		p.es_visible_web
	FROM inventario_productos p
