			LEFT JOIN (
				SELECT 
					cd.id_cotizacion,
					cd.valor 
					FROM listas_cotizaciones_detail cd
					WHERE cd.id_moneda=UNHEX("11E93A6F6D57549E838500E04C6F7E24")
			) lc ON vo.id_cotizacion=lc.id_cotizacion
			GROUP BY im.id_producto
	) lm ON p.id= lm.id_producto
	WHERE p.id = UNHEX(_id) LIMIT 1;

ELSE 

