		i.valor as impuesto_valor,
		p.nombre, 
		p.codigo,
		p.costo,
		COALESCE(p.precio,0) as precio,
		COALESCE(((p.precio-p.costo)/p.costo)*100,0) as pct_utilidad,
		COALESCE(p.costo,0) as costo_moneda,
		COALESCE(p.precio,0) as precio_moneda,
		COALESCE(p.existencia,0) as existencia,
		COALESCE(p.pct_comision,0) as pct_comision,
		p.es_inactivo,
		p.imagen,
		p.nombre_web,
		p.descripcion_web,
		p.es_visible_web
	FROM inventario_productos as p  
	LEFT JOIN inventario_categorias as c ON p.id_categoria= c.id
	LEFT JOIN sistema_listas as l ON p.id_unidad= l.id
	LEFT JOIN sistema_impuestos as i ON p.id_impuesto= i.id
	WHERE p.id = UNHEX(_id) LIMIT 1;

END IF;

END
;;
DELIMITER ;

