	FROM listas_cotizaciones_detail c 
	INNER JOIN listas_cotizaciones h ON h.id=c.id_cotizacion
	WHERE  h.id_empresa= UNHEX(_id_empresa) AND c.id_moneda=UNHEX("11E93A6F6D57549E838500E04C6F7E24")
	ORDER BY c.order_id DESC 
	LIMIT 1
);

#CONSULTA PRINCIPAL PRODUCTOS
IF (@es_modo_fiscal>0) THEN
	#MODO FISCAL
	SELECT 
		HEX(p.id) as id, 
		p.order_id, 
		p.last_update,
		HEX(p.id_empresa) as id_empresa,
		HEX(p.id_categoria) as id_categoria, 
		HEX(p.id_unidad) as id_unidad,
		HEX(p.id_impuesto) as id_impuesto,
		c.nombre as categoria_nombre, 
		l.nombre as unidad_nombre,
		p.cantidad_empaque,
		i.valor as impuesto_valor,
		p.nombre, 
		p.codigo,
		@costo:=COALESCE(lm.costo*lm.valor, COALESCE(p.costo*@last_valor_bs,0)) as costo,
		@precio:= IF( lm.costo, COALESCE(@costo * ((@pct_fiscal/100)+1),0), COALESCE(p.precio*@last_valor_bs,0) ) as precio,
		IF( lm.costo, @pct_fiscal, COALESCE(((@precio-@costo)/@costo)*100,0) ) as pct_utilidad,
		COALESCE(@costo,0) as costo_moneda,
		COALESCE(@precio,0)  as precio_moneda,
		COALESCE(p.existencia,0) as existencia,
		COALESCE(p.pct_comision,0) as pct_comision,
		p.es_inactivo,
		p.imagen,
		p.nombre_web,
		p.descripcion_web,
		p.es_visible_web
	FROM inventario_productos p
	LEFT JOIN inventario_categorias as c ON p.id_categoria= c.id
	LEFT JOIN sistema_listas as l ON p.id_unidad= l.id
	LEFT JOIN sistema_impuestos as i ON p.id_impuesto= i.id
	LEFT JOIN (
		SELECT 
				im.id_producto,
				im.costo,
				im.cantidad,
				im.precio,
				MAX(vo.nro_control) as nro_control,
				lc.valor
			FROM inventario_movimientos im
			INNER JOIN ventas_operaciones vo ON im.id_operacion = vo.id
			LEFT JOIN (
				SELECT 
					cd.id_cotizacion,
					cd.valor 
					FROM listas_cotizaciones_detail cd
					WHERE cd.id_moneda=UNHEX("11E93A6F6D57549E838500E04C6F7E24")
			) lc ON vo.id_cotizacion=lc.id_cotizacion
			GROUP BY im.id_producto
	) lm ON p.id= lm.id_producto
	WHERE p.id_empresa=UNHEX(_id_empresa)
	ORDER BY p.nombre;

ELSE 

	#MODO STANDARD
	SELECT 
		HEX(p.id) as id, 
		p.order_id, 
		p.last_update,
		HEX(p.id_empresa) as id_empresa,
		HEX(p.id_categoria) as id_categoria, 
		HEX(p.id_unidad) as id_unidad,
		HEX(p.id_impuesto) as id_impuesto,
		c.nombre as categoria_nombre, 
		l.nombre as unidad_nombre,
		p.cantidad_empaque,
		i.valor as impuesto_valor,
		p.nombre, 
		p.codigo,
		p.costo,
		COALESCE(p.precio,0) as precio,
		COALESCE(((p.precio-p.costo)/p.costo)*100,0) as pct_utilidad,
		COALESCE(p.costo,0) as costo_moneda,
		COALESCE(p.precio,0) as precio_moneda,
		COALESCE(p.existencia,0) as existencia,
		COALESCE(p.pct_comision,0) as pct_comision,
		p.es_inactivo,
		p.imagen,
		p.nombre_web,
		p.descripcion_web,
		p.es_visible_web
	FROM inventario_productos as p  
	LEFT JOIN inventario_categorias as c ON p.id_categoria= c.id
	LEFT JOIN sistema_listas as l ON p.id_unidad= l.id
	LEFT JOIN sistema_impuestos as i ON p.id_impuesto= i.id
	WHERE p.id_empresa=UNHEX(_id_empresa) AND p.es_inactivo=0
	ORDER BY p.nombre;

END IF;

END
;;
