DROP PROCEDURE IF EXISTS `inventario_productos_SEL_ALL`;
DELIMITER ;;
CREATE  PROCEDURE `inventario_productos_SEL_ALL`(IN `_id_empresa` VARCHAR(36), IN `_id_categoria` VARCHAR(36))
BEGIN
		set @order = " ORDER BY p.nombre;";
		set @empresa = CONCAT("WHERE p.id_empresa=UNHEX('", _id_empresa, "') ");
		set @categoria = CONCAT(" AND p.id_categoria=UNHEX('", _id_categoria, "') ");
		
		set @sql = "SELECT 
				HEX(p.id) as id, 
				p.order_id, 
				p.last_update,
				HEX(p.id_empresa) as id_empresa,
				HEX(p.id_producto) as id_producto,
				HEX(p.id_tipo) as id_tipo,
				t.nombre as tipo_nombre,
				HEX(p.id_categoria) as id_categoria, 
				c.nombre as categoria_nombre,
				s.codigo, 
				s.nombre, 
				p.nombre_display, 
				HEX(s.id_unidad_compra) as id_unidad_compra, 
				uc.nombre_display as unidad_compra_nombre,
				HEX(s.id_unidad_venta) as id_unidad_venta, 
				uv.nombre_display as unidad_venta_nombre,
				s.cantidad_unidad_compra,
				p.costo_pct_dcto,
				p.costo_pct_adic,
				p.dolar_costo_anterior,
				p.dolar_costo_dolar,
				@dolar_costo_bruto:= p.dolar_costo_dolar as dolar_costo_bruto,
				@dolar_costo_neto:=COALESCE(((@dolar_costo_bruto/((p.costo_pct_dcto/100)+1))*((p.costo_pct_adic/100)+1)),@dolar_costo_bruto) as dolar_costo_neto,
				p.dolar_pct_utilidad,
				@dolar_precio_inicial:=COALESCE((@dolar_costo_neto*((p.dolar_pct_utilidad/100)+1)),@dolar_costo_neto) as dolar_precio_inicial,
				@dolar_precio_final:=COALESCE((@dolar_precio_inicial*((i.valor/100)+1)),@dolar_precio_inicial) as dolar_precio_final,
				@dolar_precio_inicial as precio_venta_inicial,
				@dolar_precio_final as precio_venta_final,
				@dolar_costo_neto as costo_moneda,
				@dolar_precio_inicial as precio_moneda,
				COALESCE(p.stock_minimo,0) as stock_minimo,
				HEX(p.id_impuesto) as id_impuesto,
				i.nombre as impuesto_nombre,
				COALESCE(i.valor,0) as impuesto_valor,
				p.es_inactivo,
				p.es_editable,
				COALESCE(e.cantidad,0) as total_existencia,
				p.imagen
			FROM inventario_productos as p  
			LEFT JOIN sistema_productos as s ON p.id_producto=s.id
			LEFT JOIN sistema_listas as t ON p.id_tipo=t.id
			LEFT JOIN inventario_categorias as c ON p.id_categoria=c.id
			LEFT JOIN sistema_unidades as uc ON s.id_unidad_compra=uc.id
			LEFT JOIN sistema_unidades as uv ON s.id_unidad_venta=uv.id
			LEFT JOIN sistema_impuestos as i ON p.id_impuesto=i.id
			LEFT JOIN (SELECT ie.id_producto,SUM(ie.cantidad) as cantidad
															FROM inventario_existencias ie 
															GROUP BY ie.id_producto) e ON p.id=e.id_producto 
			";

		
	IF _id_categoria = "" THEN
		set @finalSQL= CONCAT(@sql, @empresa, @order);
	ELSE 
		set @finalSQL= CONCAT(@sql, @empresa, @categoria, @order);
	END IF;
	
	PREPARE stmt FROM @finalSQL;
	EXECUTE stmt;
	SELECT @finalSQL;
END
;;
DELIMITER ;

