			o.numero,
			o.nro_control,
			DATE_FORMAT(o.fecha,'%Y-%m-%dT%TZ') as fecha,
			o.fecha_registro,
			HEX(o.id_destino) as id_destino,
			HEX(o.id_cliente) as id_cliente,
			p.nombre as cliente_nombre,
			p.rif as cliente_rif,
		COALESCE(x.valor,0) as cotizacion_valor,
		(
			@total := COALESCE (
				(
					o.monto_exento + o.base_imponible_tg + (
						o.base_imponible_tg * (o.pct_alicuota_tg / 100)
					) + o.base_imponible_tr + (
						o.base_imponible_tr * (o.pct_alicuota_tr / 100)
					) + o.base_imponible_ta + (
						o.base_imponible_ta * (o.pct_alicuota_ta / 100)
					)
				),
				0
			)
		) AS total,
		@abonos:= (COALESCE((m.monto),0)) as abonos,
		@deuda:= (COALESCE((@total - m.monto),@total)) as deuda,
				COALESCE(@total * x.valor,0) as total_moneda,
		COALESCE(@abonos * x.valor,0) as abonos_moneda,
		COALESCE(@deuda * x.valor,0) as deuda_moneda
	FROM
		ventas_operaciones o
	LEFT JOIN (
		SELECT
			m.id_operacion,
			COALESCE (sum(m.monto), 0) AS monto
		FROM
			caja_movimientos m
		WHERE
			m.id_instrumento <> UNHEX("11E93E7A9E776D889B0D00E04C6F7E24")
		GROUP BY
			m.id_operacion
	) m ON o.id = m.id_operacion 
	LEFT JOIN sistema_operaciones as so ON o.id_tipo_operacion=so.id
	LEFT JOIN ventas_clientes as p ON o.id_cliente=p.id
	LEFT JOIN listas_cotizaciones_detail as x ON (o.id_cotizacion=x.id_cotizacion AND x.id_moneda=UNHEX(_id_moneda))
	WHERE	
		o.id_empresa=UNHEX(_id_empresa) AND
		o.id_cliente=UNHEX(_id_cliente) AND 
		o.id_status=1
	GROUP BY
		o.id
	HAVING deuda>0;


END
;;
