DROP PROCEDURE IF EXISTS `reporte_caja_cuentas_detalle`;
DELIMITER ;;
CREATE  PROCEDURE `reporte_caja_cuentas_detalle`(IN `_id_empresa` VARCHAR(36), IN `_id_moneda` VARCHAR(36), IN `_id_cuenta` VARCHAR(36), IN `_desde` DATETIME, IN `_hasta` DATETIME)
BEGIN
SELECT 
		HEX(m.id_cuenta) as cuenta_id,
		cta.numero as cuenta_numero,
		b.nombre as banco_nombre,
		m.fecha,
		i.nombre as instrumento_nombre,
		m.numero_operacion,
		o.nombre as operacion_nombre,
		v.nro_control,
		x.nombre as cliente_nombre,
		x.rif as cliente_rif,
		COALESCE(c.valor,0) as cotizacion_valor,
		COALESCE((m.monto * c.valor),0) as monto
	FROM caja_movimientos as m
	LEFT JOIN listas_cuentas_bancarias as cta ON m.id_cuenta =cta.id
	INNER JOIN sistema_listas as b ON cta.id_banco =b.id
	INNER JOIN sistema_listas as i ON m.id_instrumento=i.id
	INNER JOIN ventas_operaciones as v ON m.id_operacion=v.id
	INNER JOIN sistema_operaciones as o ON v.id_tipo_operacion=o.id
	INNER JOIN ventas_clientes as x ON v.id_cliente=x.id
	LEFT JOIN listas_cotizaciones_detail as c ON (v.id_cotizacion=c.id_cotizacion AND c.id_moneda=UNHEX(_id_moneda))
	WHERE 
		v.id_empresa = UNHEX(_id_empresa) AND
		m.id_cuenta = UNHEX(_id_cuenta) AND 
		v.id_status=1 
		AND (DATE(m.fecha) BETWEEN DATE(_desde) AND DATE(_hasta));
END
;;
