DROP PROCEDURE IF EXISTS `reporte_dashboard_caja`;
DELIMITER ;;
CREATE  PROCEDURE `reporte_dashboard_caja`(IN `_id_empresa` VARCHAR(36), IN `_desde` DATETIME, IN `_hasta` DATETIME)
BEGIN
	SELECT 
		COUNT(m.id) as cuenta,
		i.nombre as instrumento,
		SUM(IF (o.signo_caja='+', m.monto,0)) as ent,
		SUM(IF (o.signo_caja='-', m.monto,0)) as sal,
		(SUM(IF (o.signo_caja='+', m.monto,0))) - (SUM(IF (o.signo_caja='-', m.monto,0))) as total
	FROM caja_movimientos as m
	INNER JOIN sistema_listas as i ON m.id_instrumento=i.id
	INNER JOIN sistema_operaciones o ON m.id_tipo_operacion=o.id
	WHERE o.signo_caja<>"N"
		AND m.id_empresa = UNHEX(_id_empresa) 
		AND (DATE(m.fecha) BETWEEN DATE(_desde) AND DATE(_hasta))
	GROUP BY m.id_instrumento;
				
END
;;
