-- ----------------------------
-- Procedure structure for reporte_dashboard_resumen
-- ----------------------------
DROP PROCEDURE IF EXISTS `reporte_dashboard_resumen`;
DELIMITER ;;
CREATE  PROCEDURE `reporte_dashboard_resumen`(IN `_id_empresa` VARCHAR(36), IN `_desde` DATETIME, IN `_hasta` DATETIME)
BEGIN
	SELECT 
		item, 
		cantidad_item as cantidad,
		total_item as total
	
	FROM(
				SELECT 
				'inventario' as item,
				COUNT(p.id) as cantidad_item,
				SUM(ROUND(COALESCE((p.precio*p.existencia),0),2)) as total_item
				FROM inventario_productos p
			WHERE 
				p.id_empresa = UNHEX(_id_empresa)  
			GROUP BY p.id_empresa
			UNION ALL
				SELECT
				'ventas' as item,
				COUNT(DISTINCT (o.id)) as cantidad_item,
				SUM(@total:=ROUND(COALESCE(
					(
						o.monto_exento+
						o.base_imponible_tg+
						(o.base_imponible_tg*(o.pct_alicuota_tg/100))+
						o.base_imponible_tr+
						(o.base_imponible_tr*(o.pct_alicuota_tr/100))+
						o.base_imponible_ta+
						(o.base_imponible_ta*(o.pct_alicuota_ta/100))
					) 
				,0),2)) as total_item
				FROM ventas_operaciones o
				INNER JOIN sistema_operaciones x ON o.id_tipo_operacion=x.id
			WHERE 
				o.id_empresa = UNHEX(_id_empresa) 
				AND x.signo_caja="+"
				AND o.id_status=1 
				AND (DATE(o.fecha) BETWEEN DATE(_desde) AND DATE(_hasta)) 
				GROUP BY o.id_empresa
			UNION ALL
				SELECT 
				'compras' as item,
				COUNT(DISTINCT (o.id)) as cantidad_item,
				SUM(@total:=ROUND(COALESCE(
					(
						o.monto_exento+
						o.base_imponible_tg+
						(o.base_imponible_tg*(o.pct_alicuota_tg/100))+
						o.base_imponible_tr+
						(o.base_imponible_tr*(o.pct_alicuota_tr/100))+
						o.base_imponible_ta+
						(o.base_imponible_ta*(o.pct_alicuota_ta/100))

					) 
				,0),2)) as total_item
				FROM compras_operaciones o
				INNER JOIN sistema_operaciones x ON o.id_tipo_operacion=x.id
			WHERE 
				o.id_empresa = UNHEX(_id_empresa) 
				AND x.signo_inventario="+"
				AND o.id_status=1 
				AND (DATE(o.fecha) BETWEEN DATE(_desde) AND DATE(_hasta)) 
				GROUP BY o.id_empresa
		) X ;
END
;;
DELIMITER ;

