CREATE  PROCEDURE `reporte_inventario_productos_seleccion`(IN `_id_empresa` VARCHAR(36), IN `_id_categoria` VARCHAR(36))
BEGIN
		
	SET @_seleccion= 
		"SELECT 
			HEX(p.id) as id, 
			p.order_id, 
			p.last_update,
			HEX(p.id_empresa) as id_empresa,
			HEX(p.id_categoria) as id_categoria, 
			HEX(p.id_unidad) as id_unidad,
			HEX(p.id_impuesto) as id_impuesto,
			c.nombre as categoria_nombre, 
			l.nombre as unidad_nombre,
			i.valor as impuesto_valor,
			p.nombre, 
			p.codigo,
			p.costo,
			p.precio,
			p.existencia
		FROM inventario_productos as p  
		LEFT JOIN inventario_categorias as c ON p.id_categoria= c.id
		LEFT JOIN sistema_listas as l ON p.id_unidad= l.id
		LEFT JOIN sistema_impuestos as i ON p.id_impuesto= i.id";
	
	IF _id_categoria = "" THEN 
		SET @_condicion= CONCAT(" WHERE p.id_empresa=UNHEX('", _id_empresa ,"') ORDER BY c.nombre, p.nombre");
	ELSE
		SET @_condicion= CONCAT (" WHERE p.id_empresa=UNHEX('", _id_empresa ,"') AND p.id_categoria=UNHEX('", _id_categoria,"') ORDER BY p.nombre");
	END IF;
	
	SET @sql=CONCAT(@_seleccion,@_condicion);

	PREPARE stmt FROM @sql;
  EXECUTE stmt;
  SELECT @sql;

END
;;
