-- Procedure structure for ventas_clientes_DEUDAS
-- ----------------------------
DROP PROCEDURE IF EXISTS `ventas_clientes_DEUDAS`;
DELIMITER ;;
CREATE  PROCEDURE `ventas_clientes_DEUDAS`(IN `_id_cliente` VARCHAR(36))
BEGIN


SELECT 
			HEX(o.id) as id, 
			o.order_id,
			o.last_update,
			HEX(o.id_empresa) as id_empresa,
			HEX(o.id_tipo_operacion) as id_tipo_operacion,
			o.numero,
			o.nro_control,
			DATE_FORMAT(o.fecha,'%Y-%m-%dT%TZ') as fecha,
			o.fecha_registro,
			HEX(o.id_destino) as id_destino,
			HEX(o.id_cliente) as id_cliente,
			p.nombre as cliente_nombre,
			p.rif as cliente_rif,
			HEX(o.id_tipo_documento) as id_tipo_documento,
			so.nombre as tipo_documento_nombre,
			o.nro_control_documento,
			o.nro_documento,
			o.fecha_documento,
			o.nro_factura_afectada,
			hex(o.id_doc_origen) as id_doc_origen,
			hex(o.id_cotizacion) as id_cotizacion,
			COALESCE(o.monto_exento,0) as monto_exento,
			COALESCE(o.base_imponible_tg,0) as base_imponible_tg,
			o.pct_alicuota_tg,
			COALESCE(o.base_imponible_tr,0) as base_imponible_tr,
			o.pct_alicuota_tr,
			COALESCE(o.base_imponible_ta,0) as base_imponible_ta,
			o.pct_alicuota_ta,
			@monto_iva_tg:=COALESCE((o.base_imponible_tg*(o.pct_alicuota_tg/100)),0) as monto_iva_tg,
			@monto_iva_tr:=COALESCE((o.base_imponible_tr*(o.pct_alicuota_tr/100)),0) as monto_iva_tr,
			@monto_iva_ta:=COALESCE((o.base_imponible_ta*(o.pct_alicuota_ta/100)),0) as monto_iva_ta,
			@total:= COALESCE((o.monto_exento+o.base_imponible_tg+@monto_iva_tg+o.base_imponible_tr+@monto_iva_tr+o.base_imponible_ta+@monto_iva_ta),0) as total,
			o.monto_exento as monto_exento_moneda,
			o.base_imponible_tg as base_imponible_tg_moneda,
			o.base_imponible_tr as base_imponible_tr_moneda,
			o.base_imponible_ta as base_imponible_ta_moneda,
			COALESCE(@monto_iva_tg,0) as monto_iva_tg_moneda,
			COALESCE(@monto_iva_tr,0) as monto_iva_tr_moneda,
			COALESCE(@monto_iva_ta,0) as monto_iva_ta_moneda,			
			COALESCE(@total,0) as total_moneda,
			o.pct_descuento,
			o.pct_adicional,
			o.id_status,
			o.id_estado,
			hex(o.id_usuario) as id_usuario,
			u.nombre as usuario_nombre,
			o.observacion,
			@abonos:= (COALESCE((m.monto),0)) as abonos,
			@deuda:= (COALESCE((@total - m.monto),@total)) as deuda,
			@abonos as abonos_moneda,
			@deuda as deuda_moneda
	FROM ventas_operaciones as o
	LEFT JOIN (
				SELECT
					m.id_operacion,
					COALESCE (sum(m.monto), 0) AS monto
				FROM
					caja_movimientos m
				WHERE
					m.id_instrumento <> UNHEX("11E93E7A9E776D889B0D00E04C6F7E24")
				GROUP BY
					m.id_operacion
			) m ON o.id = m.id_operacion 
	LEFT JOIN sistema_operaciones as so ON o.id_tipo_operacion=so.id
	LEFT JOIN ventas_clientes as p ON o.id_cliente=p.id
	LEFT JOIN sistema_usuarios as u ON o.id_usuario=u.id
	WHERE	
		o.id_cliente=UNHEX(_id_cliente) AND 
		o.id_status=1
	GROUP BY
		o.id
	HAVING deuda>0;
END
;;
DELIMITER ;

