-- ----------------------------
-- Procedure structure for ventas_clientes_ONE
-- ----------------------------
DROP PROCEDURE IF EXISTS `ventas_clientes_ONE`;
DELIMITER ;;
CREATE  PROCEDURE `ventas_clientes_ONE`(IN `_id` VARCHAR(36))
BEGIN
	SELECT
		HEX(c.id) as id, 

					c.order_id,
					c.last_update,
					HEX(c.id_empresa) as id_empresa,
					c.nombre, 
					c.rif, 
					c.direccion,
					c.telefono,
					c.email,
					c.limite_credito,
					c.observacion,
		SUM(
			@total := COALESCE (
				(
					o.monto_exento + o.base_imponible_tg + (
						o.base_imponible_tg * (o.pct_alicuota_tg / 100)
					) + o.base_imponible_tr + (
						o.base_imponible_tr * (o.pct_alicuota_tr / 100)
					) + o.base_imponible_ta + (
						o.base_imponible_ta * (o.pct_alicuota_ta / 100)
					)
				),
				0
			)
		) AS total,
		SUM(COALESCE(m.monto,0)) as abonos,
		SUM(COALESCE((@total - m.monto),@total)) as deuda,
		SUM(COALESCE(@total,0)) as total_moneda,
		SUM(COALESCE(m.monto,0)) as abonos_moneda,
		SUM(COALESCE(COALESCE((@total - m.monto),@total),0)) as deuda_moneda
	FROM
		ventas_clientes c
	LEFT JOIN (
		SELECT 
			o.*
		FROM
			ventas_operaciones o
		WHERE	
			o.id_status=1
	)o ON o.id_cliente = c.id
	LEFT JOIN (
		SELECT
			m.id_operacion,
			COALESCE (sum(m.monto), 0) AS monto
		FROM
			caja_movimientos m
		WHERE
			m.id_instrumento <> UNHEX("11E93E7A9E776D889B0D00E04C6F7E24")
		GROUP BY
			m.id_operacion
	) m ON o.id = m.id_operacion   
	WHERE c.id = UNHEX(_id) LIMIT 1;

END
;;
DELIMITER ;

